import React from "react";
import { useState } from "react";
import styles from './Setting.css';

function Setting(){
    const [selectedImage1, setSelectedImage1] = useState(null);
    const [selectedImage2, setSelectedImage2] = useState(null);
    const [selectedImage3, setSelectedImage3] = useState(null);
    const [points, setPoints] = useState([]);


    const handleImageChange1 = (e) => {
      const file = e.target.files[0];
      const reader = new FileReader();
  
      reader.onload = (event) => {
        setSelectedImage1(event.target.result);
      };
  
      if (file) {
        reader.readAsDataURL(file);
        handleImageUpload1(file);
      }
    };
  
    const handleImageUpload1 = (file) => {
      // 서버로 파일 업로드 요청을 보내는 로직을 작성합니다.
      // 예시로는 fetch를 사용하여 POST 요청을 보내고, 응답을 처리합니다.
      const formData = new FormData();
      formData.append('image', file);
  
      fetch('/api/upload', {
        method: 'POST',
        body: formData
      })
        .then(response => response.json())
        .then(data => {
          // 업로드 후의 처리 로직을 작성합니다.
          console.log('업로드 완료:', data);
          // 추가적인 작업을 수행하거나, 서버로부터 받은 데이터를 활용할 수 있습니다.
        })
        .catch(error => {
          console.error('업로드 실패:', error);
          // 실패 시에 대한 오류 처리 로직을 작성합니다.
        });
    };
    const handleImageChange2 = (e) => {
        const file = e.target.files[0];
        const reader = new FileReader();
    
        reader.onload = (event) => {
          setSelectedImage2(event.target.result);
        };
    
        if (file) {
          reader.readAsDataURL(file);
          handleImageUpload2(file);
        }
    };
    
    const handleImageUpload2 = (file) => {
        // 서버로 파일 업로드 요청을 보내는 로직을 작성합니다.
        // 예시로는 fetch를 사용하여 POST 요청을 보내고, 응답을 처리합니다.
        const formData = new FormData();
        formData.append('image', file);
    
        fetch('/api/upload', {
          method: 'POST',
          body: formData
        })
          .then(response => response.json())
          .then(data => {
            // 업로드 후의 처리 로직을 작성합니다.
            console.log('업로드 완료:', data);
            // 추가적인 작업을 수행하거나, 서버로부터 받은 데이터를 활용할 수 있습니다.
          })
          .catch(error => {
            console.error('업로드 실패:', error);
            // 실패 시에 대한 오류 처리 로직을 작성합니다.
          });
    };

    const handleImageChange3 = (e) => {
        const file = e.target.files[0];
        const reader = new FileReader();
    
        reader.onload = (event) => {
          setSelectedImage3(event.target.result);
        };
    
        if (file) {
          reader.readAsDataURL(file);
          handleImageUpload3(file);
        }
    };
    
    const handleImageUpload3 = (file) => {
        // 서버로 파일 업로드 요청을 보내는 로직을 작성합니다.
        // 예시로는 fetch를 사용하여 POST 요청을 보내고, 응답을 처리합니다.
        const formData = new FormData();
        formData.append('image', file);
    
        fetch('/api/upload', {
          method: 'POST',
          body: formData
        })
          .then(response => response.json())
          .then(data => {
            // 업로드 후의 처리 로직을 작성합니다.
            console.log('업로드 완료:', data);
            // 추가적인 작업을 수행하거나, 서버로부터 받은 데이터를 활용할 수 있습니다.
          })
          .catch(error => {
            console.error('업로드 실패:', error);
            // 실패 시에 대한 오류 처리 로직을 작성합니다.
          });
    };
  
    const handleImageClick = (e) => {
        const image = e.target;
        const imageRect = image.getBoundingClientRect();
        const offsetX = e.clientX - imageRect.left;
        const offsetY = e.clientY - imageRect.top;
    
        if (points.length < 2) {
          const updatedPoints = [...points, { x: offsetX, y: offsetY }];
          setPoints(updatedPoints);
        } else {
          setPoints([{ x: offsetX, y: offsetY }]);
        }
    };
  
    return (
      <div>
        <h1>수영장 CCTV 설정</h1>
        <div>
            <h2>1번 화면</h2>
            <div className={styles.screen}>
                {selectedImage1 && 
                <img src={selectedImage1} alt="업로드된 이미지" className={styles.screen} onClick={handleImageClick} style={{ cursor: 'crosshair' }}/>}
            </div>
            <div>
                {points.length === 2 && (
                <div>
                    <p>첫 번째 점: {points[0].x}, {points[0].y}</p>
                    <p>두 번째 점: {points[1].x}, {points[1].y}</p>
                </div>
                )}
            </div>
            <input type="file" accept="image/*" onChange={handleImageChange1} />
        </div>
        <div>
            <h2>2번 화면</h2>
            <div className={styles.screen}>
                {selectedImage2 && <img src={selectedImage2} alt="업로드된 이미지" />}
            </div>
            <input type="file" accept="image/*" onChange={handleImageChange2} />
        </div>
        <div>
            <h2>3번 화면</h2>
            <div className={styles.screen}>
                {selectedImage3 && <img src={selectedImage3} alt="업로드된 이미지" />}
            </div>
            <input type="file" accept="image/*" onChange={handleImageChange3} />
        </div>
    </div>
    );
  };
  
export default Setting;
