                {/* 회원 추가 폼 */}
                <form onSubmit={e => {
                    e.preventDefault();
                    const name = e.target.name.value;
                    addMember(name);
                    e.target.reset();
                }}>
                    <input type="text" name="name" placeholder="이름" />
                    <button type="submit">추가</button>
                </form>
                
                {/* 회원 리스트 */}
                <ul>
                    {members.map(member => (
                    <li key={member.id}>
                        {member.name}
                        <button onClick={() => deleteMember(member.id)}>삭제</button>
                    </li>
                    ))}
                </ul>
            </div>
        </>

    )
}

export default Member;
